     // Note: Result comes through onPurchaseSuccess/onPurchaseError callbacks\n    } catch (error) {\n      console.error('Request purchase failed:', error);\n    }\n  };\n\n  return (\n    <View>\n      <Text>Store: {connected ? '\u2705 Connected' : '\u231b Connecting...'}</Text>\n\n      {/* Products */}\n      <Text>Products:</Text>\n      {products.map((product) => (\n        <View key={product.id}>\n          <Text>\n            {product.title} - {product.displayPrice}\n          </Text>\n          <Button\n            title=\"Buy\"\n            onPress={() => handlePurchase(product.id, false)}\n          />\n        </View>\n      ))}\n\n      {/* Subscriptions */}\n      <Text>Subscriptions:</Text>\n      {subscriptions.map((subscription) => (\n        <View key={subscription.id}>\n          <Text>\n            {subscription.title} - {subscription.displayPrice}\n          </Text>\n          <Button\n            title=\"Subscribe\"\n            onPress={() => handlePurchase(subscription.id, true)}\n            disabled={activeSubscriptions.some(\n              (sub) => sub.productId === subscription.id,\n            )}\n          />\n        </View>\n      ))}\n    </View>\n  );\n}\n"})}),"\n",(0,i.jsx)(n.h2,{id:"configuration-options",children:"Configuration Options"}),"\n",(0,i.jsx)(n.h3,{id:"useiapoptions",children:"useIAP(options)"}),"\n",(0,i.jsxs)(n.table,{children:[(0,i.jsx)(n.thead,{children:(0,i.jsxs)(n.tr,{children:[(0,i.jsx)(n.th,{children:"Parameter"}),(0,i.jsx)(n.th,{children:"Type"}),(0,i.jsx)(n.th,{children:"Required"}),(0,i.jsx)(n.th,{children:"Description"})]})}),(0,i.jsx)(n.tbody,{children:(0,i.jsxs)(n.tr,{children:[(0,i.jsx)(n.td,{children:(0,i.jsx)(n.code,{children:"options"})}),(0,i.jsx)(n.td,{children:(0,i.jsx)(n.code,{children:"UseIAPOptions"})}),(0,i.jsx)(n.td,{children:"No"}),(0,i.jsx)(n.td,{children:"Configuration object"})]})})]}),"\n",(0,i.jsx)(n.h4,{id:"useiapoptions-1",children:"UseIAPOptions"}),"\n",(0,i.jsx)(n.pre,{children:(0,